import React,{useState,useEffect} from "react";
import axios from 'axios';


const Search =()=>{
    const [term, setTerm]=useState('');
    const [results, setResults]=useState([]);

    useEffect(()=>{
       const search =  async ()=>{
           const{data}=await  axios.get('https://en.wikipedia.org/wapi.php', {
                params:{
                    action:'query',
                    list:'search',
                    origin:'*',
                    format:'json',
                    srsearch: term,
                }
            })
           setResults(data);
        };
           if(term){
               search();
           }
        }, [term])



    return (<div>
               <div className='ui form'>
                  <div className='field'>
                      <label>enter search term</label>
                      <input
                          value={term}
                          onChange={(event)=> setTerm(event.target.value)}
                          className='input'/>

                  </div>
                </div>
            </div>



    )
}


export default Search;
