                Edit <code>src/App.tsx</code> and save to reload.
            </p>

            <Main products={products} handleAdd={handleAdd} />
            <Cart cartItems={cartItems} handleAdd={handleAdd} handleRemove={handleRemove} />
        </div>
    );
}

export default App;
